public void ImportCertificate(byte[] certData, string password, StoreName name, StoreLocation loc)\n{\n    // Use X509KeyStorageFlags.Exportable to mark the private key as exportable\n    X509Certificate2 cert = new X509Certificate2(certData, password, X509KeyStorageFlags.Exportable);\n    \n    X509Store certStore = new X509Store(name, loc);\n\n    try\n    {\n        // Open the store for writing\n        certStore.Open(OpenFlags.ReadWrite);\n\n        // Add the certificate to the store\n        certStore.Add(cert);\n    }\n    catch (Exception ex)\n    {\n        Console.WriteLine("An error occurred: " + ex.Message);\n    }\n    finally\n    {\n        // Close the store\n        certStore.Close();\n    }\n}