using System;\nusing System.Data.SqlClient;\nusing System.Web.Configuration;\n\nclass Program\n{\n    static void Main()\n    {\n        string sql = "SELECT [UserId] FROM [UserProfiles] WHERE [UserId] <> @CurrentUserId";\n        string strCon = WebConfigurationManager.ConnectionStrings["SocialSiteConnectionString"].ConnectionString;\n\n        using (SqlConnection conn = new SqlConnection(strCon))\n        {\n            SqlCommand comm = new SqlCommand(sql, conn);\n            comm.Parameters.AddWithValue("@CurrentUserId", "CurrentUserId"); // Use parameterized queries to prevent SQL injection\n\n            try\n            {\n                conn.Open();\n                using (SqlDataReader reader = comm.ExecuteReader())\n                {\n                    while (reader.Read())\n                    {\n                        string userId = reader.GetString(0); // Assuming UserId is a string type\n                        Console.WriteLine(userId);\n                    }\n                }\n            }\n            catch (Exception ex)\n            {\n                Console.WriteLine("An error occurred: " + ex.Message);\n            }\n        }\n    }\n}