protected void Page_Load(object sender, EventArgs e)\n{\n    if (!IsPostBack)\n    {\n        // Assuming CheckBoxList1 is defined in your .aspx file.\n        CheckBoxList1.Items.Add(new ListItem("Item 1", "1"));\n        CheckBoxList1.Items.Add(new ListItem("Item 2", "2"));\n        CheckBoxList1.Items.Add(new ListItem("Item 3", "3"));\n\n        for (int i = 0; i < CheckBoxList1.Items.Count; i++)\n        {\n            // Assign different CSS classes to each item\n            string className = "";\n            switch (i)\n            {\n                case 0:\n                    className = "item-style-1";\n                    break;\n                case 1:\n                    className = "item-style-2";\n                    break;\n                case 2:\n                    className = "item-style-3";\n                    break;\n            }\n            CheckBoxList1.Items[i].Attributes.Add("class", className);\n        }\n    }\n}