using System;\nusing System.IO;\n\nclass Program\n{\n    static void Main()\n    {\n        string filePath = @"\\arfjwknasgmed17\C$\FLAG\CONDITION\CP-ARFJN-FLAG.XLS";\n\n        try\n        {\n            // Open the file with read and shared read access\n            using (FileStream fs = new FileStream(filePath, FileMode.Open, FileAccess.Read, FileShare.Read, 4096))\n            using (StreamReader reader = new StreamReader(fs))\n            {\n                string content = reader.ReadToEnd();\n                Console.WriteLine(content);\n            }\n        }\n        catch (IOException ex)\n        {\n            Console.WriteLine($"An error occurred: {ex.Message}");\n        }\n    }\n}