using System.Diagnostics;\n\npublic void MonitorPerformanceCounters()\n{\n    var counters = new[]\n    {\n        "ASP.NET Applications(*)\\Requests/sec",\n        "ASP.NET\\Requests in Application Queue",\n        "ASP.NET Applications(*)\\Request Execution Time",\n        "ASP.NET Applications(*)\\Active Anonymous Sessions",\n        "ASP.NET Applications(*)\\Application Restarts",\n        "ASP.NET Applications(*)\\Errors Total"\n    };\n\n    foreach (var path in counters)\n    {\n        PerformanceCounter counter = new PerformanceCounter();\n        var parts = path.Split('\\');\n        counter.CategoryName = parts[0];\n        if (parts.Length > 1) counter.CounterName = parts[1];\n        if (parts.Length > 2) counter.InstanceName = parts[2].Replace("(*)", "All_Instances");\n        \n        Console.WriteLine($"{path}: {counter.NextValue()}");\n    }\n}