using System;\nusing System.IO;\n\nclass Program\n{\n    static void Main()\n    {\n        // List of file paths to read\n        string[] filePaths = { "file1.txt", "file2.txt", "file3.txt", "file4.txt", "file5.txt" };\n        \n        // Output file path\n        string outputFile = "combinedFile.txt";\n\n        using (StreamWriter writer = new StreamWriter(outputFile))\n        {\n            foreach (string path in filePaths)\n            {\n                if (File.Exists(path))\n                {\n                    using (StreamReader reader = new StreamReader(path))\n                    {\n                        string content = reader.ReadToEnd();\n                        writer.Write(content);\n                    }\n                }\n                else\n                {\n                    Console.WriteLine($"Warning: The file {path} does not exist.");\n                }\n            }\n        }\n\n        Console.WriteLine("Files have been combined successfully into " + outputFile);\n    }\n}