using System;\nusing System.IO;\nusing System.Text;\n\nclass Program\n{\n    static void Main()\n    {\n        byte[] data = { 72, 101, 108, 108, 111 }; // Example byte array for "Hello"\n        \n        var writer = new StreamWriter(Application.StartupPath + @"\B323.txt");\n        string content = Encoding.ASCII.GetString(data); // Use the appropriate encoding\n        writer.Write(content);\n        writer.Close();\n        writer.Dispose();\n    }\n}