using OpenQA.Selenium;\nusing OpenQA.Selenium.Chrome;\n\npublic class AjaxTest\n{\n    public static void Main()\n    {\n        IWebDriver driver = new ChromeDriver();\n        driver.Navigate().GoToUrl("http://example.com");\n\n        // Interact with the page that triggers an AJAX call\n        IWebElement triggerButton = driver.FindElement(By.Id("ajax-trigger"));\n        triggerButton.Click();\n\n        // Wait for a specific element to be updated as a result of the AJAX call\n        var wait = new WebDriverWait(driver, TimeSpan.FromSeconds(10));\n        wait.Until(SeleniumExtras.WaitHelpers.ExpectedConditions.TextToBePresentInElementLocated(By.Id("ajax-result"), "Expected Text"));\n\n        // Perform further assertions or actions\n\n        driver.Quit();\n    }\n}