public bool FindSpecificRowValue(DataTable dataTable, string searchValue, out int rowNumber)\n{\n    rowNumber = -1; // Initialize to an invalid row number\n    for (int i = 0; i < dataTable.Rows.Count; i++)\n    {\n        foreach (DataColumn column in dataTable.Columns)\n        {\n            if (dataTable.Rows[i][column].ToString() == searchValue)\n            {\n                rowNumber = i;\n                return true;\n            }\n        }\n    }\n    return false;\n}