using System;\nusing System.Diagnostics;\n\nclass Program\n{\n    static void Main()\n    {\n        // Get all instances of WINWORD.EXE\n        Process[] processes = Process.GetProcessesByName("winword");\n\n        foreach (Process proc in processes)\n        {\n            try\n            {\n                // Kill each instance of the process\n                proc.Kill();\n                proc.WaitForExit(); // Wait for the process to exit\n                Console.WriteLine($"Killed a WINWORD.EXE process with ID: {proc.Id}");\n            }\n            catch (Exception ex)\n            {\n                Console.WriteLine($"Error terminating process: {ex.Message}");\n            }\n        }\n\n        if (processes.Length == 0)\n        {\n            Console.WriteLine("No instances of WINWORD.EXE are running.");\n        }\n    }\n}