// Assuming this is in a method where you handle postback (e.g., Button Click)\nprotected void btnSubmit_Click(object sender, EventArgs e)\n{\n    int numberOfQuestions = 5; // Example number of questions\n    for(int i = 1; i <= numberOfQuestions; i++)\n    {\n        string selectedAnswer = Request.Form["Question" + i];\n        if (selectedAnswer != null)\n        {\n            // Compare the selected answer with your correct answers array\n            string correctAnswer = CorrectAnswersArray[i-1]; // Assuming this is where correct answers are stored\n\n            if(selectedAnswer == correctAnswer)\n            {\n                Response.Write("Question " + i + ": Correct!");\n            }\n            else\n            {\n                Response.Write("Question " + i + ": Incorrect.");\n            }\n        }\n    }\n}