// In your Page_Load event to create questions and answers\nprotected void Page_Load(object sender, EventArgs e)\n{\n    if (!IsPostBack)\n    {\n        string htmlContent = "";\n        int[] questionIds = GenerateRandomQuestions();\n        for(int i = 0; i < questionIds.Length; i++)\n        {\n            Question q = GetQuestionFromDatabase(questionIds[i]);\n            htmlContent += $"<label>{q.Text}</label>";\n            for (int j = 0; j < q.Answers.Count; j++)\n            {\n                htmlContent += $"<input type='radio' name='Question{i + 1}' value='{q.Answers[j]}'/> {q.Answers[j]}<br/>";\n            }\n\n            // Storing correct answer in a hidden field\n            HiddenField hf = new HiddenField();\n            hf.ID = "HiddenAnswer" + (i + 1);\n            hf.Value = q.CorrectAnswer;\n            myDiv.Controls.Add(hf);\n        }\n        myDiv.InnerHtml = htmlContent;\n    }\n}\n\nprotected void btnSubmit_Click(object sender, EventArgs e)\n{\n    int numberOfQuestions = 5; // Example number of questions\n    for(int i = 1; i <= numberOfQuestions; i++)\n    {\n        string selectedAnswer = Request.Form["Question" + i];\n        if (selectedAnswer != null)\n        {\n            HiddenField hf = (HiddenField)myDiv.FindControl("HiddenAnswer" + i);\n            string correctAnswer = hf.Value; // Correct answer stored in hidden field\n\n            if(selectedAnswer == correctAnswer)\n            {\n                Response.Write("Question " + i + ": Correct!");\n            }\n            else\n            {\n                Response.Write("Question " + i + ": Incorrect.");\n            }\n        }\n    }\n}