public class FormSubmissionReceiver : SPItemEventReceiver\n   {\n       public override void ItemAdded(SPItemEventProperties properties)\n       {\n           base.ItemAdded(properties);\n\n           // Get the source and destination libraries\n           SPList sourceLibrary = (SPList)properties.Web.Lists["Form2"];\n           SPList destinationLibrary = (SPList)properties.Web.Lists["Form1"];\n\n           // Copy item from Source to Destination\n           var fieldsToCopy = new Dictionary<string, object>();\n           foreach (SPField field in sourceLibrary.Fields)\n           {\n               if (!field.ReadOnlyField &&\n                   !field.Hidden &&\n                   field.InternalName != "Attachments" &&\n                   !fieldsToCopy.ContainsKey(field.InternalName))\n               {\n                   fieldsToCopy[field.InternalName] =\n                       properties.ListItem[field.InternalName];\n               }\n           }\n\n           SPListItem destItem = destinationLibrary.Items.Add(fieldsToCopy);\n           destItem.Update();\n\n           // Optionally delete from source if needed\n           this.DisableEventFiring();\n           try { properties.ListItem.Delete(); }\n           finally { this.EnableEventFiring(); }\n       }\n   }