using System.Data;\nusing MySql.Data.MySqlClient; // Ensure this package is installed via NuGet\n\nstring connectionString = "your_connection_string_here";\nstring query = @"SELECT TABLE_NAME, COLUMN_NAME, DATA_TYPE, CHARACTER_MAXIMUM_LENGTH, IS_NULLABLE \n                 FROM INFORMATION_SCHEMA.COLUMNS \n                 WHERE TABLE_SCHEMA = 'your_database_name'";\n\nusing (MySqlConnection connection = new MySqlConnection(connectionString))\n{\n    MySqlCommand command = new MySqlCommand(query, connection);\n    MySqlDataAdapter adapter = new MySqlDataAdapter(command);\n    DataSet schemaDataSet = new DataSet();\n    \n    adapter.Fill(schemaDataSet);\n\n    // schemaDataSet now contains your database schema\n}