using System;\nusing System.Diagnostics;\nusing System.Linq;\n\nclass Program\n{\n    static void Main()\n    {\n        // Retrieve all network interfaces on the system\n        PerformanceCounterCategory category = new PerformanceCounterCategory("Network Interface");\n        string[] instanceNames = category.GetInstanceNames();\n\n        foreach (var instance in instanceNames)\n        {\n            if (!instance.Contains("isatap") && !instance.Contains("teredo"))\n            {\n                Console.WriteLine($"Interface: {instance}");\n\n                using (PerformanceCounter bytesSentCounter = new PerformanceCounter("Network Interface", "Bytes Sent/sec", instance))\n                using (PerformanceCounter bytesReceivedCounter = new PerformanceCounter("Network Interface", "Bytes Received/sec", instance))\n                {\n                    // First read initializes the counter\n                    bytesSentCounter.NextValue();\n                    bytesReceivedCounter.NextValue();\n\n                    System.Threading.Thread.Sleep(1000);  // Sleep for a second\n\n                    float sentBytesPerSec = bytesSentCounter.NextValue();\n                    float receivedBytesPerSec = bytesReceivedCounter.NextValue();\n\n                    Console.WriteLine($"Bytes Sent/Sec: {sentBytesPerSec}");\n                    Console.WriteLine($"Bytes Received/Sec: {receivedBytesPerSec}");\n                }\n            }\n        }\n    }\n}