// Adding CapsuleCollider component to the player\nvoid AddCapsuleColliderToPlayer(GameObject player)\n{\n    CapsuleCollider capsuleCollider = player.AddComponent<CapsuleCollider>();\n    capsuleCollider.radius = 1.0f; // Adjust size as needed\n    capsuleCollider.height = 2.0f; // Height from foot to head\n    capsuleCollider.center = new Vector3(0, capsuleCollider.height / 2.0f, 0); // Position center at middle of the character\n}