var data = \n    from details in h_details.Descendants("ROW")\n    join inst in instance.XPathSelectElements("//Row")\n    on new { Id = details.Element("ID").Value, Language = details.Element("LANGUAGE").Value }\n    equals new { Id = inst.XPathSelectElement("Field[@Name='h_id']").Value, Language = inst.XPathSelectElement("Field[@Name='h_lang']").Value }\n    select new\n    {\n        DetailId = details.Element("ID").Value,\n        DetailLanguage = details.Element("LANGUAGE").Value,\n        // Add other fields you need from both XMLs\n    };