using System;\nusing System.Windows.Forms;\nusing Syncfusion.Windows.Forms.PdfViewer;\n\npublic class DocumentViewerForm : Form {\n    private PdfDocumentViewAdv pdfViewerControl;\n\n    public DocumentViewerForm() {\n        this.pdfViewerControl = new PdfDocumentViewAdv();\n        this.Text = "Document Viewer";\n        this.WindowState = FormWindowState.Maximized;\n\n        // Load a PDF document\n        this.pdfViewerControl.Load("sample.pdf");\n\n        this.Controls.Add(this.pdfViewerControl);\n    }\n\n    [STAThread]\n    static void Main() {\n        Syncfusion.Licensing.SyncfusionLicenseProvider.RegisterLicense("YOUR_SYNCFUSION_LICENSE_KEY_HERE");\n        Application.EnableVisualStyles();\n        Application.SetCompatibleTextRenderingDefault(false);\n        Application.Run(new DocumentViewerForm());\n    }\n}