using System;\nusing System.IO;\nusing System.Web;\nusing iTextSharp.text;\nusing iTextSharp.text.pdf;\n\nnamespace YourNamespace.Controllers {\n    public class ReportController : Controller {\n        public ActionResult GenerateReport() {\n            string folderPath = Server.MapPath("~/Reports/"); // Ensure this path exists and is writable\n            string fileName = String.Format("{0}.pdf", Guid.NewGuid());\n            string filePath = Path.Combine(folderPath, fileName);\n\n            Document document = new Document(PageSize.A4);\n            PdfWriter.GetInstance(document, new FileStream(filePath, FileMode.Create));\n            document.Open();\n            document.Add(new Phrase("Hello World!"));\n            document.Close();\n\n            ViewBag.PdfUrl = Url.Content(Path.Combine("~/Reports/", fileName));\n            return View("ReportView"); // Your view name showing the HTML content with a download link\n        }\n    }\n}