using System;\nusing System.IO;\nusing System.Windows.Forms;\n\npublic class XmlDisplayForm : Form\n{\n    private WebBrowser browserControl = new WebBrowser();\n\n    public XmlDisplayForm()\n    {\n        // Setup the WebBrowser control to dock fill and add it to the form.\n        browserControl.Dock = DockStyle.Fill;\n        this.Controls.Add(browserControl);\n\n        // Load XML and CSS files\n        string xmlContent = File.ReadAllText("path/to/your/document.xml");\n        string cssContent = File.ReadAllText("path/to/your/style.css");\n\n        // Inject CSS into the XML for proper rendering\n        string htmlDisplay = "<html><head><style>" + cssContent + "</style></head><body>" + xmlContent + "</body></html>";\n\n        // Navigate to HTML content containing XML and CSS\n        browserControl.Document.Write(htmlDisplay);\n    }\n\n    [STAThread]\n    static void Main()\n    {\n        Application.EnableVisualStyles();\n        Application.SetCompatibleTextRenderingDefault(false);\n        Application.Run(new XmlDisplayForm());\n    }\n}