using (var context = new YourDbContext())\n{\n    var startDate = DateTime.Parse("2009-01-14");\n    var endDate = DateTime.Parse("2009-01-15");\n\n    var result = context.Hits\n        .Where(h => h.Date >= startDate && h.Date < endDate)\n        .GroupBy(h => h.Page)\n        .Select(g => new { Page = g.Key, Number = g.Count() })\n        .OrderByDescending(x => x.Number)\n        .Take(10)\n        .ToList();\n\n    // result now contains the top 10 pages with the most hits within the specified date range\n}