using System;\n\nclass Subscription {\n    static void Main() {\n        // Assume today is the purchase date\n        DateTime purchaseDate = DateTime.Now;\n        \n        // Add 6 months to the purchase date\n        DateTime expiryDate = purchaseDate.AddMonths(6);\n        \n        Console.WriteLine("Purchase Date: " + purchaseDate.ToString("yyyy-MM-dd"));\n        Console.WriteLine("Expiry Date: " + expiryDate.ToString("yyyy-MM-dd"));\n    }\n}