public class DataConsumer\n{\n    private DataSet _cachedData;\n    private DataServiceClient _service;\n\n    public DataConsumer()\n    {\n        _service = new DataServiceClient();\n        _service.SubscribeForChanges(DataChangedHandler);\n        _cachedData = _service.GetData();  // Initial fetch.\n    }\n\n    private void DataChangedHandler()\n    {\n        // Update cache in response to notification.\n        _cachedData = _service.GetData();\n    }\n}