using System;\nusing System.Data;\n\nclass Program\n{\n    static void Main()\n    {\n        // Assuming DataSet ds, Table1 and Table2 already exist and a relation has been added\n        DataSet ds = new DataSet();\n\n        // Create DataTables with columns and primary keys\n        DataTable table1 = new DataTable("Table1");\n        table1.Columns.Add("ID", typeof(int));\n        DataColumn pkColumn1 = table1.Columns["ID"];\n        pkColumn1.AutoIncrement = true;\n        table1.PrimaryKey = new DataColumn[] { pkColumn1 };\n\n        DataTable table2 = new DataTable("Table2");\n        table2.Columns.Add("ID", typeof(int));\n        table2.Columns.Add("Name", typeof(string));\n        DataColumn fkColumn = table2.Columns["ID"];\n\n        // Adding the tables to DataSet\n        ds.Tables.Add(table1);\n        ds.Tables.Add(table2);\n\n        // Add the relation between Table1 and Table2 using ID field\n        ds.Relations.Add(new DataRelation("Table1ToTable2", pkColumn1, fkColumn));\n\n        // Example data insertion\n        DataRow row1 = table1.NewRow();\n        table1.Rows.Add(row1);\n\n        DataRow row2 = table2.NewRow();\n        row2["ID"] = 1; // Matching ID with Table1's auto-generated ID\n        row2["Name"] = "Example Name";\n        table2.Rows.Add(row2);\n\n        // Accessing data using relations\n        foreach (DataRow parentRow in table1.Rows)\n        {\n            DataRow[] childRows = parentRow.GetChildRows("Table1ToTable2");\n            foreach (DataRow childRow in childRows)\n            {\n                Console.WriteLine($"ID: {childRow["ID"]}, Name: {childRow["Name"]}");\n            }\n        }\n    }\n}