using System;\nusing System.ServiceProcess;\n\npublic class MyWindowsService : ServiceBase\n{\n    public MyWindowsService()\n    {\n        this.ServiceName = "MyDownloadService";\n    }\n\n    protected override void OnStart(string[] args)\n    {\n        // Example action to perform on service start\n        System.Timers.Timer timer = new System.Timers.Timer();\n        timer.Interval = 24 * 60 * 60 * 1000; // 24 hours in milliseconds\n        timer.Elapsed += Timer_Elapsed;\n        timer.Start();\n    }\n\n    private void Timer_Elapsed(object sender, System.Timers.ElapsedEventArgs e)\n    {\n        // Your logic to download dataset and insert into SQL Server goes here\n    }\n\n    protected override void OnStop()\n    {\n        // Example clean up on service stop\n    }\n}\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        ServiceBase.Run(new MyWindowsService());\n    }\n}