DataTable dt = new DataTable();\n\n// Define columns without identity\nDataColumn dc2 = new DataColumn();\ndc2.DataType = typeof(string);\ndc2.ColumnName = "strRow";\ndt.Columns.Add(dc2);\n\n// Populate rows\nfor (int i = 0; i < myStringArray.Length; i++)\n{\n    DataRow dr = dt.NewRow();\n    dr["strRow"] = myStringArray[i];\n    dt.Rows.Add(dr);\n}\n\nusing (SqlConnection conn = new SqlConnection(connString))\nusing (SqlCommand cmd = new SqlCommand("dbo.InsertPIF", conn))\n{\n    cmd.CommandType = CommandType.StoredProcedure;\n    \n    // Use SqlParameter to bind the DataTable\n    var param = cmd.Parameters.AddWithValue("@InputFileParam", dt);\n    param.SqlDbType = SqlDbType.Structured;\n\n   .conn.Open();\n    int RowsAffected = cmd.ExecuteNonQuery();\n    Console.WriteLine(RowsAffected + " rows inserted.");\n}