using System;\nusing System.Text.RegularExpressions;\n\npublic class AddressParser\n{\n    public static string[] ParseServiceAddress(string address)\n    {\n        // Define the regex pattern\n        string pattern = @"^(\d+)\s?([NSEW])?\s([\w\s]+)\s(Ave|St|Rd|Blvd|Ln|Dr|Ct|Pkwy)(\s[NSEW])?$";\n        Regex regex = new Regex(pattern);\n        Match match = regex.Match(address.Trim());\n\n        if (match.Success)\n        {\n            return new string[]\n            {\n                match.Groups[1].Value,  // Street Number\n                match.Groups[2].Value,  // Pre-direction (if any)\n                match.Groups[3].Value.Trim(),  // Street Name\n                match.Groups[4].Value,  // Street Type\n                match.Groups[5].Value ?? ""  // Post-direction (if any)\n            };\n        }\n        else\n        {\n            throw new ArgumentException("Address format is not recognized.");\n        }\n    }\n\n    public static void Main()\n    {\n        string address = "123 N Main St";\n        var result = ParseServiceAddress(address);\n        Console.WriteLine($"Street Number: {result[0]}");\n        Console.WriteLine($"Pre-direction: {result[1]}");\n        Console.WriteLine($"Street Name: {result[2]}");\n        Console.WriteLine($"Street Type: {result[3]}");\n        Console.WriteLine($"Post-direction: {result[4]}");\n    }\n}