using System;\nusing System.Text.RegularExpressions;\n\npublic class HtmlProcessor\n{\n    public string Processing(string Source, string Tag, string Attribute)\n    {\n        // This pattern matches the specified tag with the specified attribute and its value.\n        string pattern = $@"(<{Tag}(?=[^>]*\s+{Attribute}\s*=\s*([""'])(\\.|(?!\1)[^\\])*?\1)[^>]*)";\n        return Regex.Replace(Source, pattern, "$1");\n    }\n\n    public static void Main()\n    {\n        string before = @"<input type=""text"" name=""Input"" id=""Input"" onMouseOver=""some js to be eliminated"">";\n        HtmlProcessor processor = new HtmlProcessor();\n        string after = processor.Processing(before, "input", "onMouseOver");\n        Console.WriteLine(after); // expected output: <input type="text" name="Input" id="Input">\n    }\n}