using System;\nusing System.Runtime.InteropServices;\n\npublic class ManagingComObject\n{\n    [DllImport("ole32.dll")]\n    private static extern int ReleaseComObject(object o);\n\n    public void UseComObject()\n    {\n        dynamic excelApp = null;\n        try\n        {\n            excelApp = Activator.CreateInstance(Type.GetTypeFromProgID("Excel.Application"));\n            // Use the COM object, e.g., excelApp.Quit();\n        }\n        finally\n        {\n            if (excelApp != null)\n            {\n                GC.Collect();\n                GC.WaitForPendingFinalizers();\n\n                while (Marshal.ReleaseComObject(excelApp) > 0);\n\n                excelApp = null;\n            }\n        }\n    }\n}