using (SqlConnection connection = new SqlConnection(connectionManager.SqlConnection.ConnectionString))\n{\n    using (SqlCommand command = new SqlCommand(Enum.GetName(typeof(StoredProcs), storedProc), connection))\n    {\n        // Set the CommandTimeout property (value in seconds)\n        command.CommandTimeout = 120; // Example: set timeout to 120 seconds\n\n        // Add parameters if necessary\n        foreach (var param in parameterValues)\n        {\n            command.Parameters.AddWithValue(param.Key, param.Value);\n        }\n\n        connection.Open();\n        return command.ExecuteScalar();\n    }\n}