using System;\nusing System.IO;\nusing System.Text;\nusing System.Web.UI;\nusing System.Xml;\n\npublic partial class DisplayXMLPage : Page\n{\n    protected void Page_Load(object sender, EventArgs e)\n    {\n        if (!IsPostBack)\n        {\n            // Assuming you have an XmlDocument object named xmlDocument\n            XmlDocument xmlDocument = new XmlDocument();\n            xmlDocument.LoadXml("<root><child>Value</child></root>");\n\n            // Convert XmlDocument to a formatted string\n            string formattedXml = FormatXml(xmlDocument);\n\n            // Display formatted XML in a Literal control\n            literalControl.Text = Server.HtmlEncode(formattedXml);\n        }\n    }\n\n    private string FormatXml(XmlDocument xmlDocument)\n    {\n        StringBuilder sb = new StringBuilder();\n        XmlWriterSettings settings = new XmlWriterSettings\n        {\n            Indent = true,\n            IndentChars = "  ",\n            NewLineChars = "\r\n",\n            NewLineHandling = NewLineHandling.Replace\n        };\n\n        using (XmlWriter writer = XmlWriter.Create(sb, settings))\n        {\n            xmlDocument.Save(writer);\n        }\n\n        return sb.ToString();\n    }\n}