using System;\nusing System.ComponentModel.Composition.Hosting;\nusing System.Linq;\n\npublic object GetExportByType(CompositionContainer container, Type type)\n{\n    // Use the GetExportedValues<object>() to get all exports and filter by type.\n    var export = container.GetExportedValues<object>()\n                          .FirstOrDefault(value => value.GetType() == type);\n    \n    return export;\n}\n\n// Usage\nType someType = typeof(IMyType); // or dynamically obtained Type\nvar exportedObject = GetExportByType(_container, someType);\n\nif (exportedObject != null)\n{\n    // Use the exported object\n}\nelse\n{\n    Console.WriteLine("No export found for type: " + someType);\n}