using System;\nusing System.Data.SqlClient;\n\npublic class DatabaseQueryExecutor\n{\n    // Connection string - update this with your database connection details\n    private static readonly string connectionString = "your_connection_string_here";\n\n    public void ExecuteQuery(string query)\n    {\n        using (SqlConnection connection = new SqlConnection(connectionString))\n        {\n            try\n            {\n                connection.Open();\n                SqlCommand command = new SqlCommand(query, connection);\n                \n                // If it's a non-query (like SELECT), use ExecuteReader or ExecuteScalar.\n                int result = command.ExecuteNonQuery(); // For INSERT/UPDATE/DELETE\n\n                Console.WriteLine($"{result} rows affected.");\n            }\n            catch (Exception ex)\n            {\n                Console.WriteLine($"An error occurred: {ex.Message}");\n            }\n        }\n    }\n}