public class MainForm {\n    public void Initialize() {\n        List<IModule> loadedModules = ModuleLoader.LoadModules(Application.StartupPath + @"\modules");\n        \n        foreach (IModule module in loadedModules) {\n            Button button = new Button();\n            button.Text = module.Name;\n            button.Click += (sender, args) => ShowModule(module);\n            \n            // Add the button to your form's controls or menu\n            this.Controls.Add(button);\n            \n            module.Initialize();  // Initialize each module\n        }\n    }\n\n    private void ShowModule(IModule module) {\n        switch (module.Name) {\n            case "Registration":\n                new RegistrationForm().Show();\n                break;\n            case "Classroom":\n                new ClassroomForm().Show();\n                break;\n            // Add more cases as needed\n        }\n    }\n}