public class EntityValidator {\n    public static void ValidateEntity(YourEntity entity) {\n        var properties = typeof(YourEntity).GetProperties();\n        foreach (var property in properties) {\n            var maxLengthAttribute = property.GetCustomAttributes(typeof(MaxLengthAttribute), false);\n            if (maxLengthAttribute.Length > 0 && property.GetValue(entity) is string stringValue &&\n                stringValue.Length > ((MaxLengthAttribute)maxLengthAttribute[0]).Value) {\n                throw new ValidationException($"{property.Name} exceeds maximum length.");\n            }\n        }\n    }\n}