public class SecureAttribute : AuthorizeAttribute\n{\n    protected override void HandleUnauthorizedRequest(AuthorizationContext filterContext)\n    {\n        if (!filterContext.HttpContext.Request.IsAuthenticated)\n        {\n            filterContext.Result = new HttpUnauthorizedResult();\n            // Optionally add a custom header with the login URL or return it in JSON/XML response.\n            filterContext.HttpContext.Response.Headers.Add("X-Allow-Redirect", Url.Action("Login", "Account"));\n        }\n    }\n}