using mshtml;\n\npublic void GetElementPosition(HTMLAnchorElementClass anchorElement)\n{\n    if (anchorElement != null && anchorElement.document != null)\n    {\n        IHTMLDocument2 document = (IHTMLDocument2)anchorElement.document;\n        HTMLWindow2 window = (HTMLWindow2)document.parentWindow;\n\n        // JavaScript code to get the position of the element\n        string script = "var rect = arguments[0].getBoundingClientRect();" +\n                         "return { top: rect.top, left: rect.left };";\n\n        object[] args = new object[] { anchorElement };\n        object result = window.eval(script, ref args);\n\n        if (result != null && result is HTMLElement)\n        {\n            // Assuming the result is a JSON-like object with top and left\n            string positionJson = result.ToString();\n            // Parse positionJson to extract top and left values\n            // You can use Newtonsoft.Json or another JSON parser for this\n            dynamic position = Newtonsoft.Json.JsonConvert.DeserializeObject(positionJson);\n            double top = position.top;\n            double left = position.left;\n\n            Console.WriteLine($"Top: {top}, Left: {left}");\n        }\n    }\n}