using System;\nusing System.Runtime.InteropServices;\n\npublic class StorageCardInfo\n{\n    // Assuming a hypothetical function GetStorageCardSerialNumber\n    [DllImport("coredll.dll")]\n    private static extern int GetStorageCardSerialNumber(byte[] serialBuffer, int bufferSize);\n\n    public string GetSerialNumber()\n    {\n        byte[] buffer = new byte[256]; // Adjust size as necessary\n        int result = GetStorageCardSerialNumber(buffer, buffer.Length);\n        \n        if (result == 0) // Assuming 0 means success\n        {\n            return System.Text.Encoding.ASCII.GetString(buffer).TrimEnd('\0'); // Convert to string and trim null characters\n        }\n        else\n        {\n            throw new Exception("Failed to retrieve storage card serial number");\n        }\n    }\n}