using System;\nusing System.Net;\nusing System.Net.Mail;\n\nclass Program\n{\n    static void Main()\n    {\n        string from = "yourYahooEmail@yahoo.com";\n        string password = "yourYahooPassword";\n        string to = "recipientEmail@example.com";\n        string subject = "Test Subject";\n        string body = "Hello, this is a test email sent through Yahoo SMTP.";\n\n        MailMessage mailMessage = new MailMessage(from, to, subject, body);\n\n        using (SmtpClient smtpClient = new SmtpClient("smtp.mail.yahoo.com", 587))\n        {\n            smtpClient.EnableSsl = true;\n            smtpClient.Credentials = new NetworkCredential(from, password);\n            try\n            {\n                smtpClient.Send(mailMessage);\n                Console.WriteLine("Email sent successfully!");\n            }\n            catch (Exception ex)\n            {\n                Console.WriteLine($"Failed to send email with the following error: {ex.Message}");\n            }\n        }\n    }\n}