using System.Net;\nusing System.Net.Mail;\n\npublic void SendEmailWithHtmlContent(string recipientEmail, string subject, string htmlBody)\n{\n    var message = new MailMessage();\n    message.To.Add(new MailAddress(recipientEmail));\n    message.Subject = subject;\n    message.Body = htmlBody;\n    message.IsBodyHtml = true;\n\n    using (var smtpClient = new SmtpClient("smtp.yourmailserver.com"))\n    {\n        smtpClient.Port = 587; // or the appropriate port for your server\n        smtpClient.EnableSsl = true;\n        smtpClient.Credentials = new NetworkCredential("your-email@example.com", "your-password");\n        \n        try\n        {\n            smtpClient.Send(message);\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine($"Email send failed: {ex.Message}");\n        }\n    }\n}