using System.Net.Http;\nusing System.Text;\nusing System.Threading.Tasks;\n\npublic async Task UpdateStatusAsync()\n{\n    var client = new HttpClient();\n    client.BaseAddress = new System.Uri("https://api.cliqset.com/");\n    \n    // Set necessary HTTP headers\n    client.DefaultRequestHeaders.Authorization = \n        new System.Net.Http.Headers.AuthenticationHeaderValue(\n            "Basic", "Y**********rZXQ6ZnVsY3J1bTA=");\n    client.DefaultRequestHeaders.Add("CliqsetConsumerKey", "11**********3c2ccd26c4ca946e3d8e");\n\n    // Create the payload\n    var json = "{\"status\":\"Updated status\"}";\n\n    // Prepare request content\n    HttpContent content = new StringContent(json, Encoding.UTF8, "application/json");\n\n    // Send PUT request\n    HttpResponseMessage response = await client.PutAsync("200905/user/cosmorocket/status", content);\n\n    if (response.IsSuccessStatusCode)\n    {\n        Console.WriteLine("Status updated successfully.");\n    }\n    else\n    {\n        Console.WriteLine($"Error: {response.StatusCode} - {await response.Content.ReadAsStringAsync()}");\n    }\n}