// GET: Create the object to be confirmed\n[HttpGet]\npublic ActionResult Create()\n{\n    return View(new MyObject());\n}\n\n// POST: Collect data from user and show confirm page\n[HttpPost]\npublic ActionResult Confirm(MyObject myObject)\n{\n    if (ModelState.IsValid)\n    {\n        // Store in TempData or ViewBag\n        TempData["MyObject"] = myObject;\n        return RedirectToAction("Confirm");\n    }\n    return View("Create", myObject);\n}\n\n// GET: Show confirmation page\n[HttpGet]\npublic ActionResult Confirm()\n{\n    var myObject = TempData["MyObject"] as MyObject;\n    if (myObject == null)\n    {\n        // Handle error, maybe redirect to Create again\n        return RedirectToAction("Create");\n    }\n    return View(myObject);\n}\n\n// POST: Finalize creation after confirmation\n[HttpPost]\npublic ActionResult Confirm(MyObject myObject)\n{\n    var tempMyObject = TempData["MyObject"] as MyObject;\n    if (tempMyObject == null || !ModelState.IsValid)\n    {\n        // Handle error, redirect to Create or Show Confirmation again\n        return RedirectToAction("Confirm");\n    }\n\n    using (var context = new YourDbContext())\n    {\n        context.MyObjects.Add(tempMyObject);\n        context.SaveChanges();\n    }\n    \n    return RedirectToAction("Index"); // Or wherever appropriate\n}