protected void Page_Load(object sender, EventArgs e)\n{\n    if (!IsPostBack)\n        return;\n\n    // Assume these are your form fields\n    string name = Request.Form["name"];\n    string email = Request.Form["email"];\n\n    if (IsValidName(name) && IsValidEmail(email))\n    {\n        Response.Redirect("success.html");\n    }\n    else\n    {\n        // Store the data in session or viewstate - Here we use Session for example\n        Session["FormData"] = new { Name = name, Email = email };\n\n        // Redirect back to form.html with a query string to indicate validation failed\n        Response.Write("<script type='text/javascript'>window.location.href='form.html?failed=true';</script>");\n    }\n}\n\nprivate bool IsValidName(string name)\n{\n    return !string.IsNullOrEmpty(name); // Example validation\n}\n\nprivate bool IsValidEmail(string email)\n{\n    try\n    {\n        var addr = new System.Net.Mail.MailAddress(email);\n        return addr.Address == email;\n    }\n    catch\n    {\n        return false;\n    }\n}