using System;\nusing System.Security.Cryptography;\nusing System.Text;\n\npublic class RequestVerification\n{\n    private bool VerifyChecksum(int reqNumber, string reqText, string clientPassword, string reqChecksum)\n    {\n        // Create a new HMACSHA256 object using the provided password as key.\n        using (HMACSHA256 hmac = new HMACSHA256(Encoding.UTF8.GetBytes(clientPassword)))\n        {\n            // Combine and encode the data into bytes\n            byte[] dataBytes = Encoding.UTF8.GetBytes(reqNumber.ToString() + reqText);\n            \n            // Compute the hash of the input data.\n            byte[] hashBytes = hmac.ComputeHash(dataBytes);\n\n            // Convert the computed hash to a hex string\n            StringBuilder hashBuilder = new StringBuilder();\n            for (int i = 0; i < hashBytes.Length; i++)\n            {\n                hashBuilder.Append(hashBytes[i].ToString("x2"));\n            }\n\n            // Compare the calculated checksum with the provided one\n            return hashBuilder.ToString().Equals(reqChecksum, StringComparison.OrdinalIgnoreCase);\n        }\n    }\n}