public interface IShoppingCartElement\n{\n    string GetDisplayText();\n}\n\npublic class CartItem : IShoppingCartElement\n{\n    public double Weight { get; set; }\n    public decimal Price { get; set; }\n\n    public string GetDisplayText()\n    {\n        return $"Item - Weight: {Weight}kg, Price: ${Price}";\n    }\n}\n\npublic class DescriptionText : IShoppingCartElement\n{\n    public string Text { get; set; }\n\n    public string GetDisplayText()\n    {\n        return Text;\n    }\n}\n\npublic class ShoppingCart\n{\n    private readonly List<IShoppingCartElement> _elements = new();\n\n    public void Add(IShoppingCartElement element)\n    {\n        _elements.Add(element);\n    }\n\n    public decimal CalculateTotalPrice()\n    {\n        decimal total = 0;\n        foreach (var item in _elements.OfType<CartItem>())\n        {\n            total += item.Price;\n        }\n        return total;\n    }\n\n    public double CalculateTotalWeight()\n    {\n        double total = 0;\n        foreach (var item in _elements.OfType<CartItem>())\n        {\n            total += item.Weight;\n        }\n        return total;\n    }\n\n    public string GetDisplayTextForListView()\n    {\n        var sb = new StringBuilder();\n        foreach (var element in _elements)\n        {\n            sb.AppendLine(element.GetDisplayText());\n        }\n        return sb.ToString();\n    }\n}