var query = (from s in db.ForumStatsSet\n             join t in db.Topics on s.TopicId equals t.topicID\n             join f in db.Forums on t.forumID equals f.forumID\n             join g in db.ForumGroups on f.forumGroupID equals g.forumGroupID\n             where s.LogDate >= date1 && s.LogDate <= date2\n             group s by new { \n                 t.topicID, \n                 t.subject, \n                 f.forumName, \n                 g.name,\n                 f.forumID\n             } into g\n             orderby g.Count() descending\n             select new TopicStatsData {\n                 TopicId = g.Key.topicID,\n                 Count = g.Count(),\n                 Subject = g.Key.subject,\n                 ForumGroupName = g.Key.name,\n                 ForumName = g.Key.forumName,\n                 ForumId = g.Key.forumID\n             });