public partial class YourTable\n    {\n        public YourTable()\n        {\n            // Manually setting UpdateCheck to Never or WhenChanged\n            this.PropertyChanging += (sender, e) =>\n            {\n                switch (e.PropertyName)\n                {\n                    case "YourFieldName":\n                        ((System.ComponentModel.IColumnDescriptor)this.GetType().GetProperty(e.PropertyName).GetCustomAttributes(typeof(System.Data.Linq.Mapping.ColumnAttribute), false)[0]).UpdateCheck = System.Data.Linq.Mapping.UpdateCheck.Never;\n                        break;\n                }\n            };\n        }\n    }