public void New(string ApplicationStartupPath, string FileName, string Department, string Month, string Year)\n{\n    // Ensure you concatenate .mdb to the path if necessary\n    string dbFilePath = System.IO.Path.Combine(ApplicationStartupPath, "Podcasts.mdb");\n    \n    string sql = "INSERT INTO PodcastsDir (FileName, Department, Month, Year) VALUES (@FileName, @Department, @Month, @Year)";\n    using (OleDbConnection conn = new OleDbConnection("Provider=Microsoft.Jet.OLEDB.4.0; Data Source=" + dbFilePath))\n    using (OleDbCommand cmd = new OleDbCommand(sql, conn))\n    {\n        // Add parameters with correct OleDbType and set values\n        cmd.Parameters.AddWithValue("@FileName", FileName);\n        cmd.Parameters.AddWithValue("@Department", Department);\n        cmd.Parameters.AddWithValue("@Month", Month);\n        cmd.Parameters.AddWithValue("@Year", Year);\n\n        conn.Open();\n        cmd.ExecuteNonQuery();\n    }\n}