using System;\nusing System.Collections.Generic;\nusing System.IO;\n\npublic class ImageChecker\n{\n    private static readonly Dictionary<byte[], string> FileTypes = new Dictionary<byte[], string>\n    {\n        { new byte[] { 0xFF, 0xD8, 0xFF }, "jpg" },\n        { new byte[] { 137, 80, 78, 71, 13, 10, 26, 10 }, "png" },\n        { new byte[] { 71, 73, 70, 56 }, "gif" },\n        { new byte[] { 66, 77 }, "bmp" }\n        // Add more file types as needed\n    };\n\n    public static bool IsImageFile(string filePath)\n    {\n        var fileBytes = File.ReadAllBytes(filePath);\n        foreach (var fileType in FileTypes)\n        {\n            if (fileBytes.Length >= fileType.Key.Length &&\n                fileBytes.Take(fileType.Key.Length).SequenceEqual(fileType.Key))\n            {\n                return true;\n            }\n        }\n\n        return false;\n    }\n}