using System;\nusing System.Windows;\n\nnamespace YourNamespace\n{\n    public partial class MainWindow : Window\n    {\n        private double _savedRightColumnWidth;\n\n        public double RightColumnWidth { get; set; }\n\n        public MainWindow()\n        {\n            InitializeComponent();\n            DataContext = this;\n            \n            // Load saved width here or use a default if not available.\n            // For example purposes, setting a default value.\n            RightColumnWidth = 200;\n        }\n\n        private void ToggleButton_Checked(object sender, RoutedEventArgs e)\n        {\n            _savedRightColumnWidth = RightColumnWidth;\n            RightColumn.Width = new GridLength(0);\n        }\n\n        private void ToggleButton_Unchecked(object sender, RoutedEventArgs e)\n        {\n            if (Double.IsNaN(_savedRightColumnWidth))\n                RightColumn.Width = new GridLength(RightColumnWidth); // default or last saved width\n            else\n                RightColumn.Width = new GridLength(_savedRightColumnWidth);\n        }\n    }\n}